import Foundation

struct AvailableOptionsJson {
  let otherDay: OtherDayOption?
  let postOffice: PostOfficeOption?
  let packStation: PackStationOption?
  let handoutLocation: HandoutLocationOption?
}

struct OtherDayOption {
  let timeTable: [DayOption]
}

struct DayOption {
  let date: String
  let standard: TimeFrameOption
  let evening: TimeFrameOption?
}

struct TimeFrameOption {
  let from: NSDate
  let to: NSDate
  let costs: Int
  let sponsorName: String?
}

struct PostOfficeOption {
  let earlyPickup: Costs
  let standard: Costs
}

struct PackStationOption {
  let standard: Costs
}

struct HandoutLocationOption {
  let standard: Costs
  let details: Location
}

struct Costs {
  let available: Bool
  let costs: Int
  let sponsorName: String?
}