import Foundation

enum CatalogueType : String {
  case Parcels = "parcels"
}

struct CataloguePrice {
  let priceExcl: Int?
  let priceIncl: Int?
  let vat: String?
}

struct CatalogueProduct {
  let productClassification: String?
  let productCode: String
  let description: String?
  let basePrice: CataloguePrice?
  let additionalPrice: CataloguePrice?
}

struct CatalogueWeightClass {
  let averageWeight: Double?
  let fromWeight: Double?
  let toWeight: Double?
  let caption: String?
  let presentationType: String?
  let products: [CatalogueProduct]
}

struct CatalogueCountry {
  let isoCode: String?
  let regionCode: String?
  let note: String?
}

struct CatalogueRegion {
  let regionCode: String?
  let weightClasses: [CatalogueWeightClass]
}

struct CatalogueResponse {
  let countries: [CatalogueCountry]
  let regions: [CatalogueRegion]
}
