import Foundation

// Unknown | New | ReadyForPayment | Pending | Paid | Cancelled | NoPaymentRequired | Refused
enum PaymentStatus : String {

  case Unknown = "Unknown"
  case New = "New"

  case ReadyForPayment = "ReadyForPayment" // klaar voor de betaling
  case Pending = "Pending" // de betaling is pending. let op: de server behandeld pending nu nog als refused.
  case Paid = "Paid" // cash
  case Cancelled = "Cancelled" // gebruiker heeft de betaling geannulleerd.

  case NoPaymentRequired = "NoPaymentRequired"

  case Refused = "Refused" // de bank heeft de betaling geweigerd
}

struct PaymentProvider {
  let brandCode: String
  let issuers: [PaymentProviderIssuer]

  func isIdeal() -> Bool {
    return self.brandCode.lowercaseString == "ideal"
  }
}

struct PaymentProviderIssuer {
  let issuerId: String
  let name: String
}
