struct Profile {
  let email: String
  let firstName: String?
  let middleName: String?
  let lastName: String?
  let primaryAddress: ProfileAddress
  let phoneNumber: String?
}

struct ProfileAddress {
  let street: String
  let houseNumber: String
  let houseNumberSuffix: String?
  let postalCode: String
  let town: String
  let country: String
}