import Foundation

enum SendPrintLabelStatus : String {
  case OrderStatusInvalid = "OrderStatusInvalid"
  case ShipmentStatusInvalid = "ShipmentStatusInvalid"
  case OrderPreannounceDateExpired = "OrderPreannounceDateExpired"
  case ShipmentPreannounceDateExpired = "ShipmentPreannounceDateExpired"
  case AllowedToPrint = "AllowedToPrint"
}

enum SendOrderStatus : String {
  case New = "New"
  case ReadyForPayment = "ReadyForPayment"
  case Paid = "Paid"
  case Cancelled = "Cancelled"
  case Processed = "Processed"
  case Failed = "Failed"
  case PendingPayment = "PendingPayment"
}

struct SendOrder {
  let orderDate: NSDate
  let orderNumber: String
  let preannounceExpirationDate: NSDate?
  let shipments: [SendShipment]
  let senderAddress:  SendAddress
  let printLabelStatus: SendPrintLabelStatus
  let orderToken: SendOrderToken
  let orderStatus: SendOrderStatus
  let agreedToTerms: Bool
  let label: SendLabel?
}

struct SendLabel {
  let retailLabelBarcode: String?
  let downloadParcelLabel: String?
}

struct SendAddress {
  let companyName: String?
  let firstName: String?
  let middleName: String?
  let lastName: String?
  let email: String?
  let street: String?
  let houseNumber: String
  let houseNumberSuffix: String?
  let postalCode: String
  let town: String?
  let country: String
}

struct SendShipment {
  let deliveryAddress: SendAddress
  let product: SendProduct
  let preannounceExpirationDate: NSDate?
  let description: String?
  let totalPrice: Int
}

struct SendOrderToken {
  let value: String
}

enum SendProductClassification : String {
  case Standard = "Standard"
  case Registered = "Registered"
}

enum SendHandlingType : String {
  case Parcel = "Parcel"
  case Letter = "Letter"
}

enum SendPresentationType : String {
  case Parcel = "Parcel"
  case Letter = "Letter"
  case LetterboxParcel = "LetterboxParcel"
}

struct SendProduct {
  let catalogueNameKey: String
  let productCode: String
  let productKey: String
  let productClassification: SendProductClassification
  let handlingType: SendHandlingType
  let presentationType: SendPresentationType
  let country: String
  let fromWeight: Double
  let averageWeight: Double
  let toWeight: Double
}

