import Foundation

struct SendOrderRequestProduct {
  let catalogueNameKey: String
  let country: String
  let averageWeight: Double
  let productCode: String
}

struct SendOrderRequestShipment {
  let product: SendOrderRequestProduct
  let deliveryAddress: SendAddress
}

struct SendOrderRequest {
  let senderAddress: SendAddress
  let shipments: [SendOrderRequestShipment]
  let agreedToTerms: Bool
}