import Foundation

// Definition for extra fields that aren't parsed into Shipment ManagedObject
struct ShipmentFields {
  let amounts: AmountsJson
  let extraInformation: [ExtraInformationJson]
  let notifications: Messages
}

struct AmountsJson {
  let reimbursement: Int?
  let insured: Int?
}

struct ExtraInformationJson {
  let data: ExtraInformationDataJson
  let type: String
}

struct ExtraInformationDataJson {
  let amount: Int?
  let text: String?
}
